class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(o){o.preventDefault();$.ajax({type:"post",url:"/comment/add-comment",data:$(this).serialize(),success:function(o){let n=t.newCommentDom(o.data.comment);t.deleteComment($(" .delete-comment-button",n)),new ToggleLike($(" .toggle-like-button",n)),$("#post-comments-"+e).prepend(n),new Noty({theme:"relax",text:"Comment added!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return console.log(e),$(`\n            <li id="comment-${e._id}">\n                <div id="comment">\n                    <small id="comment_creator_detail">\n                        <span id="comment-creator">${e.user.user_name}</span>\n                        <a class="delete-comment-button" href="/comment/destroy/${e._id}">X</a>   \n                    </small>\n                    <p>\n                        ${e.content}\n                    </p>\n                   \n                </div>\n                <div id="comment_likes" class="like_section">\n                    <a href="/like/toggle/?id=${e._id}&type=Comment" class="toggle-like-button" data-likes="0" href>0 Like</a>\n                </div>\n            </li> \n        `)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}